\begin{algorithm}
\SetAlgoLined
\KwIn{A collection of sentence graphs $D   \{S_1, S_2, ..., S_n\}$, where $S_i   (V_i, E_i)$.}
// Initialize document graph with artificial root\\
$V \gets \bigcup_{i1}^n V_i \cup \{-1\}$\\
$E \gets \bigcup_{i1}^n E_i \cup \{(-1, root(S_i)) \mid i \in [n]\}$\\
// Collect subgraphs and aligned text spans\\
p $\gets \{n \in V_i \mid label(n)   person\}$\\
c $\gets \{descendants(n) \mid n \in$ p$\}$\\
// Assign cluster ids to subgraphs via spans\\
spans $\gets \{\{\bigcup span(n) \mid n \in C_i\} \mid C_i \in $ c$\}$\\
ids $\gets \{cluster\_id(span(p_i))$ if $p_i$ is clustered else $cluster\_id(C_i) \mid C_i \in$ c$\}$\\
// Partition coreferent type matches\\
$P \gets \{\}$\\
\For{$k \in cluster\_ids$}{
   cluster $\gets \{C_i \in$ c $\mid ids[i]   k\}$\\
   $C \gets \{n \in C_i \mid C_i \in$ cluster$\}$\\
   \For{unique $l \in \{label(n) \mid n \in C\}$}{
   $P \gets P \cup \{\{n \in C \mid label(n)   l\}\}$
   }
}
// Concept label partition on unmerged nodes\\
$U \gets \{n \in V \mid n \notin \bigcup_i \{P_i \in P\}\}$\\
\For{unique $l \in \{label(n) \mid n \in U\}$}{
   $P \gets P \cup \{\{n \in U \mid label(n)   l\}\}$
}
// Merge from partition\\
$V \gets \{i | P_i \in P\}$\\
$E \gets \{(i, j) | P_i, P_j \in P, \exists k \in P_i, l \in P_j \ni (k, l) \in E\}$\\
\Return{$(V, E)$}\\
\caption{Combined merging}
\label{alg:merge}
\end{algorithm}
