\def\eventcolor{WildStrawberry}
\def\opiumcolor{Purple}
\usetikzlibrary{calc}

\newcommand{\Opium}[3]{
\node[amr] (#3_n1) at (#1, #2) {opium};
\node[amr] (#3_n2) at (#1, #2 - 0.5) { mass-quantity};
\node[amr] (#3_n3) at (#1, #2 - 1.0) {ton};
\node[amr] (#3_n4) at (#1, #2 - 1.5) {metric};
\draw[arc] (#3_n1) -- (#3_n2);
\draw[arc] (#3_n2) -- (#3_n3);
\draw[arc] (#3_n3) -- (#3_n4);


\draw[\opiumcolor,thick,dotted] ($(#3_n1.north west)+(-0.3,0.1)$)   rectangle ($(#3_n4.south east)+(0.3,-0.1)$);

}

\newcommand{\police}[3]{
\node[amr] (#3_n1) at (#1, #2) {police};
\node[amr] (#3_n2) at (#1, #2 - 0.5) {counter-01};
\node[amr] (#3_n3) at (#1, #2 - 1.0) {narcotic};
\draw[arc] (#3_n1) -- (#3_n2);
\draw[arc] (#3_n2) -- (#3_n3);

\draw[orange,thick,dotted] ($(#3_n1.north west)+(-0.2,0.1)$)   rectangle ($(#3_n3.south east)+(0.2,-0.1)$);

}

\newcommand{\country}[3]{
\node[amr] (#3_n1) at (#1, #2) {country};
\node[amr] (#3_n2) at (#1, #2 - 0.5) {name};
\node[amr] (#3_n3) at (#1, #2 - 1.0) {``Afghanistan''};
\draw[arc] (#3_n1) -- (#3_n2);
\draw[arc] (#3_n2) -- (#3_n3);
\draw[blue,thick,dotted] ($(#3_n1.north west)+(-0.3,0.1)$)   rectangle ($(#3_n3.south east)+(0.0,-0.1)$);
}


